const fetchCovidTH=e=>{fetch(e).then(e=>{if(200!==e.status)return console.log("Look like there was a problem. Status Code: "+e.status),e.json();e.json().then(function(e){document.getElementById("confirmed-th").innerHTML=(new Intl.NumberFormat).format(e.confirmed.value)+" คน",document.getElementById("recovered-th").innerHTML=(new Intl.NumberFormat).format(e.recovered.value)+" คน",document.getElementById("deaths-th").innerHTML=(new Intl.NumberFormat).format(e.deaths.value)+" คน",document.getElementById("date-th").innerHTML="อัพเดทเมื่อวันที่ "+e.lastUpdate})}).catch(e=>{throw new e})},fetchCovidGlobal=e=>{fetch(e).then(e=>{if(200!==e.status)return console.log("Look like there was a problem. Status Code: "+e.status),e.json();e.json().then(function(e){document.getElementById("confirmed-global").innerHTML=(new Intl.NumberFormat).format(e.confirmed.value)+" คน",document.getElementById("recovered-global").innerHTML=(new Intl.NumberFormat).format(e.recovered.value)+" คน",document.getElementById("deaths-global").innerHTML=(new Intl.NumberFormat).format(e.deaths.value)+" คน"})})};fetchCovidTH("https://covid19.mathdro.id/api/countries/TH"),fetchCovidGlobal("https://covid19.mathdro.id/api");